import React,{ useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import styled from 'styled-components';
import Sidebar from '../../../components/utils/Sidebar';
import AdminContent from '../../../components/utils/AdminContent';
import axios from 'axios';
import { useParams } from 'react-router-dom';
const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;
  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Label = styled.label`
  margin-bottom: 10px;
  font-size: 1.2rem;
  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 1rem;
  @media (max-width: 768px) {
    padding: 8px;
    font-size: 0.9rem;
  }
`;

const Textarea = styled.textarea`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 1rem;
  @media (max-width: 768px) {
    padding: 8px;
    font-size: 0.9rem;
  }
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #61dafb;
  border: none;
  border-radius: 4px;
  color: white;
  cursor: pointer;
  font-size: 1rem;

  &:hover {
    background-color: #21a1f1;
  }

  @media (max-width: 768px) {
    padding: 8px 16px;
    font-size: 0.9rem;
  }
`;

function EditAdmin() {
  const { register, handleSubmit,watch ,setValue  } = useForm();
  const { id } = useParams();
  const [form, setForm] = useState({title: '', image: null, description: ''})
  const image = watch('image');
  useEffect(() => {
    getArticle()

  }, []);
  const getArticle = async () =>{
    
    const response = await axios.get(`${process.env.REACT_APP_API}/api/articles/edit/${id}`);

      if (response.status === 200) {
        const { data}  = response
        setValue('title', data.title);
        setValue('image', data.image);
        setValue('description', data.description);
        console.log('aqui',data);
      } else {
        console.error('Erro ao publicar o artigo');
      }
  
    
  }

  const onSubmit = async (data) => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    formData.append('title', data.title);
    formData.append('description', data.description);
    formData.append('image', data.image[0]); 

    try {
      const response = await axios.post(`${process.env.REACT_APP_API}/api/articles/edit/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        },
      });

      if (response.status === 201) {
        alert('Artigo publicado com sucesso');
      } else {
        console.error('Erro ao publicar o artigo');
      }
    } catch (error) {
      console.error('Erro ao enviar o formulÃ¡rio', error);
    }
  };
  return (
    <Container>
      <Sidebar />
      <AdminContent>
        <Title>Publicar Novo Artigo</Title>
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Label htmlFor="title">TÃ­tulo</Label>
          <Input id="title" {...register('title', { required: true })} />

          <Label htmlFor="image">Imagem</Label>
          <Input id="image" type="file" {...register('image', { required: true })} />

          <Label htmlFor="description">DescriÃ§Ã£o</Label>
          <Textarea id="description" rows="5" {...register('description', { required: true })} />

          <Button type="submit">Publicar</Button>
        </Form>
      </AdminContent>
    </Container>
  );
}

export default EditAdmin;
