          value={email}
          onChange={(e) => setUserEmail(e.target.value)}
        />
        <Label htmlFor="password">Senha</Label>
        <Input
          id="password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button type="submit">Register</Button>
       
        </Form>
    );
};

export default Register;
