import axios from 'axios';

const API_URL = 'http://localhost:8000/api'; 
const OAUTH_URL = 'http://localhost:8000/oauth/token';

// Função para registrar um novo usuário
export const register = async (user) => {
    const response = await axios.post(`${API_URL}/register`, user);
    return response.data;
};

// Função para fazer login
export const login = async (credentials) => {
    try {
        const response = await axios.post(OAUTH_URL, {
            grant_type: 'password',
            client_id: '3',  // Substitua por seu client ID
            client_secret: 'cSarGjNrhkLRnP65WHBj7yCv2qHpzyCXhB5WYzNB',  // Substitua por seu client secret
            username: credentials.email,
            password: credentials.password,
        });

        // Armazene o token no localStorage
        localStorage.setItem('token', response.data.access_token);

        // Adicione o token aos headers padrão do axios
        axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.access_token}`;

        return response.data;
    } catch (error) {
        console.error('Error logging in:', error);
        throw error; // Lança o erro para ser tratado no componente chamador
    }
};

// Função para obter o usuário autenticado
export const getUser = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
        throw new Error('No token found');
    }

    const response = await axios.get(`${API_URL}/user`, {
        headers: {
            Authorization: `Bearer ${token}`
        }
    });
    return response.data;
};

// Função para fazer logout
export const logout = async () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
};
