import React from 'react';
import { format } from 'date-fns';
import { toZonedTime, formatInTimeZone } from 'date-fns-tz';
import { ptBR } from 'date-fns/locale';

const DataBrasileira = ({ data }) => {
  // Defina o fuso horário desejado (por exemplo, 'America/Sao_Paulo' para o horário de Brasília)
  const timeZone = 'America/Sao_Paulo';
  
  // Converta a data UTC para o fuso horário desejado
  const zonedDate = toZonedTime(new Date(data), timeZone);
  
  // Formate a data
  const dataFormatada = formatInTimeZone(zonedDate, timeZone, 'dd/MM/yyyy HH:mm:ss', { locale: ptBR });

  return <span>{dataFormatada}</span>;
};

export default DataBrasileira;
