import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import axios from 'axios';
const Container = styled.div`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
 flex-direction: column;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;

  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const PostList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
 
`;

const PostItem = styled.div`
  border: 1px solid #ddd;
  border-radius: 4px;
  padding: 15px;
  background-color: #f9f9f9;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  width: calc(33.333% - 46px); /* 3 items per row with gaps */

  @media (max-width: 1024px) {
    width: calc(50% - 20px); /* 2 items per row for tablets */
  }

  @media (max-width: 768px) {
    width: 100%; /* 1 item per row for mobile */
  }
`;

const PostTitle = styled.h3`
  margin: 0;
`;

const PostImage = styled.img`
  height: auto;
  border-radius: 4px;
  margin: 10px 0;
`;

const PostDescription = styled.p`
  margin: 10px 0;
`;

const ReadMoreLink = styled(Link)`
  color: #1abc9c;
  text-decoration: none;
  font-weight: bold;
  &:hover {
    text-decoration: underline;
  }
`;

function PublicPosts() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getArticle()
    
  }, []);
  const getArticle = async () => {  

    try {
      const response = await axios.get(`${process.env.REACT_APP_API}/api/articles`);

      if (response.status === 200) {
        const { data } = response
        await setPosts(data);       
      }
    } catch (error) {
      console.error('Erro', error);
    }
  }
  const handleFilterChange = (event) => {
    const value = event.target.value.toLowerCase();
    setFilter(value);

    const filtered = posts.filter(post =>
      post.title.toLowerCase().includes(value)
    );
    setFilteredPosts(filtered);
  };

  return (
    <Container>
      <Title>Artigos</Title>
      <PostList>
        {posts.map(post => (
          <PostItem key={post.id}>
            <PostTitle>{post.title}</PostTitle>
            <PostImage width={100} src={`${process.env.REACT_APP_API}`+post.image} alt={post.title} />
            <PostDescription>{post.description}</PostDescription>
            <ReadMoreLink to={`/details/${post.title}`}>Leia mais</ReadMoreLink>
          </PostItem>
        ))}
      </PostList>
    </Container>
  );
}

export default PublicPosts;
