import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import styled from 'styled-components';
import Sidebar from '../../../components/utils/Sidebar';
import AdminContent from '../../../components/utils/AdminContent';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;
  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Label = styled.label`
  margin-bottom: 10px;
  font-size: 1.2rem;
  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 1rem;
  @media (max-width: 768px) {
    padding: 8px;
    font-size: 0.9rem;
  }
`;

const Textarea = styled.textarea`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 1rem;
  @media (max-width: 768px) {
    padding: 8px;
    font-size: 0.9rem;
  }
`;
const CommentsSection = styled.div`
  margin-top: 40px;
`;

const CommentList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const CommentItem = styled.li`
  border-bottom: 1px solid #ccc;
  padding: 10px 0;
`;

const CommentForm = styled.form`
  margin-top: 20px;
  display: flex;
  flex-direction: column;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #61dafb;
  border: none;
  border-radius: 4px;
  color: white;
  cursor: pointer;
  font-size: 1rem;

  &:hover {
    background-color: #21a1f1;
  }

  @media (max-width: 768px) {
    padding: 8px 16px;
    font-size: 0.9rem;
  }
`;

function EditAdmin() {
  const { register, handleSubmit, watch, setValue } = useForm();
  const navigate = useNavigate();
  const { id } = useParams();
  const image = watch('image');
  const [comments, setComments] = useState([]);
  useEffect(() => {
    getArticle()

  }, []);
  const getArticle = async () => {

    const response = await axios.get(`${process.env.REACT_APP_API}/api/articles/edit/${id}`);

    if (response.status === 200) {
      const { data } = response
      setValue('title', data.title);
      setValue('image', data.image);
      setValue('description', data.description);
      await getComments()

    } else {
      console.error('Erro ao publicar o artigo');
    }


  }
  const getComments = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API}/api/article/comments/${id}`);
      if (response.status === 200) {
        setComments(response.data);
      }
    } catch (error) {
      console.error('Erro', error);
    }
  };

  const onSubmit = async (data) => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    formData.append('title', data.title);
    formData.append('description', data.description);
    formData.append('image', data.image[0]);

    try {
      const response = await axios.post(`${process.env.REACT_APP_API}/api/articles/edit/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        },
      });

      if (response.status === 201) {
        alert('Artigo publicado com sucesso');
        navigate('/admin/postlist/')
      } else {
        alert('Erro ao editado o artigo');
      }
    } catch (error) {
      console.error('Erro ao enviar o formulÃ¡rio', error);
    }
  };
  return (
    <Container>
      <Sidebar />
      <AdminContent>
        <Title>Publicar Novo Artigo</Title>
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Label htmlFor="title">TÃ­tulo</Label>
          <Input id="title" {...register('title', { required: true })} />

          <Label htmlFor="image">Imagem</Label>
          <Input id="image" type="file" {...register('image', { required: true })} />

          <Label htmlFor="description">DescriÃ§Ã£o</Label>
          <Textarea id="description" rows="5" {...register('description', { required: true })} />

          <Button type="submit">Editar</Button>
        </Form>
        <CommentsSection>
          <h3>ComentÃ¡rios</h3>
          <CommentList>
            {comments.map(comment => (
              <CommentItem key={comment.id}>
                {comment.content}
              </CommentItem>
            ))}
          </CommentList>

          <CommentForm onSubmit={handleCommentSubmit}>
            <CommentTextArea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Adicione um comentÃ¡rio..."
            />
            <CommentButton type="submit">Comentar</CommentButton>
          </CommentForm>
        </CommentsSection>
      </AdminContent>
    </Container>
  );
}

export default EditAdmin;
