import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import Sidebar from '../../../components/utils/Sidebar';
import AdminContent from '../../../components/utils/AdminContent';
const Container = styled.div`
  padding: 20px;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const TableHeader = styled.th`
  background-color: #f4f4f4;
  padding: 10px;
  border: 1px solid #ddd;
`;

const TableCell = styled.td`
  padding: 10px;
  border: 1px solid #ddd;
`;

const TableRow = styled.tr`
  &:nth-child(even) {
    background-color: #f9f9f9;
  }
`;

const FilterInput = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: 100%;
`;

function PostList() {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    // Simulação de fetch de dados
    const fetchPosts = async () => {
      // Substitua pelo seu método de fetch real
      const fetchedPosts = [
        { id: 1, title: 'Post 1', image: 'http://placekitten.com/200/200', description: 'Descrição do Post 1' },
        { id: 2, title: 'Post 2', image: 'http://placekitten.com/200/200', description: 'Descrição do Post 2' },
        { id: 3, title: 'Post 3', image: 'http://placekitten.com/200/200', description: 'Descrição do Post 3' },
        // Adicione mais posts conforme necessário
      ];
      setPosts(fetchedPosts);
      setFilteredPosts(fetchedPosts);
    };

    fetchPosts();
  }, []);

  const handleFilterChange = (event) => {
    const value = event.target.value.toLowerCase();
    setFilter(value);

    const filtered = posts.filter(post =>
      post.title.toLowerCase().includes(value)
    );
    setFilteredPosts(filtered);
  };

  return (
    <Container>
      <Sidebar />
       <Title>Listagem de Posts</Title>
        <FilterInput
          type="text"
          placeholder="Filtrar por nome..."
          value={filter}
          onChange={handleFilterChange}
        />
        <Table>
          <thead>
            <TableRow>
              <TableHeader>ID</TableHeader>
              <TableHeader>Título</TableHeader>
              <TableHeader>Imagem</TableHeader>
              <TableHeader>Descrição</TableHeader>
            </TableRow>
          </thead>
          <tbody>
            {filteredPosts.map(post => (
              <TableRow key={post.id}>
                <TableCell>{post.id}</TableCell>
                <TableCell>{post.title}</TableCell>
                <TableCell><img src={post.image} alt={post.title} width="100" /></TableCell>
                <TableCell>{post.description}</TableCell>
              </TableRow>
            ))}
          </tbody>
        </Table>
 
    </Container>
  );
}

export default PostList;
